use tempdb
go

CREATE TABLE dbo.string_truncation_test
( id int not null identity(1,1) CONSTRAINT PK_string_truncation_test PRIMARY KEY 
, tenchars varchar(10) not null
)
insert into dbo.string_truncation_test (tenchars)
values 
('123456789')
,('1234567890')
--,('12345678901') --note the error message here! Displays only 10 chars of data, after being limited by data type's truncation. 
/*
Msg 2628, Level 16, State 1, Line 10
String or binary data would be truncated in table 'tempdb.dbo.string_truncation_test', column 'tenchars'. Truncated value: '1234567890'.
The statement has been terminated.
*/

go
select * from dbo.string_truncation_test
GO 
DROP TABLE dbo.string_truncation_test
GO



